/*[Common.xml][getPaging-Prefix][페이징헤더][TANINE]*/ SELECT TTT.* FROM ( SELECT TT.*
, CEIL(TT.ID / #{rows, jdbcType=NUMERIC}) PAGE , @ROW_NUMBER TOTAL_COUNT , CEIL(@ROW_NUMBER / #{rows, jdbcType=NUMERIC}) TOTAL_PAGE
FROM ( SELECT T.* , (@ROW_NUMBER := @ROW_NUMBER + 1) ID FROM (

    	/*[Common.xml][getPaging-Prefix][페이징FOOTER][TANINE]*/
				               ) T
				             , (SELECT @ROW_NUMBER := 0) RN
		               ) TT
		       ) TTT

         WHERE TTT.PAGE = #{page ,jdbcType=NUMERIC}
