mapper.select:
select AP.applicantNo ,M.memberName ,M.memberBirth ,AP.applicantId ,AP.applyNo ,AP.applicantMillitary ,AP.applicantVeteran ,AP.applicantDisability ,AP.applicantJobProtect ,AP.applyDate ,AP.finalApplyChecked
from ta_recruit_applicant AP ,ta_recruit_notice N ,ta_recruit_member M where 1=1 and AP.recruitNo=N.recruitNO and AP.recruitNo=#{RECRUITNO} and AP.applicantId = M.memberId
AND AP.RECRUITNO = #{RECRUITNO ,jdbcType=NUMERIC}

mapper.insert:
insert into ta_recruit_applicant
(
	  RECRUITNO
	, APPLICANTNO
	, APPLICANTID
	, FINALAPPLYCHECKED
	, APPLYNO
	, APPLICANTMILLITARY
	, APPLICANTVETERAN
	, APPLICANTDISABILITY
	, APPLICANTJOBPROTECT
	, APPLYDATE


)
values
(
	  #{recruitno}
	, #{applicantno}
	, #{applicantid}
	, #{finalapplychecked}
	, #{applyno}
	, #{applicantmillitary}
	, #{applicantveteran}
	, #{applicantdisability}
	, #{applicantjobprotect}
	, SYSDATE()


)

mapper.update:
update ta_recruit_applicant
set   UPDATEDATE = SYSDATE()




	, APPLICANTMILLITARY = #{applicantmillitary, jdbcType=VARCHAR}


	, APPLYNO = #{applyno, jdbcType=VARCHAR}


	, APPLICANTVETERAN = #{applicantveteran, jdbcType=VARCHAR}


	, APPLICANTDISABILITY = #{applicantdisability, jdbcType=VARCHAR}


	, APPLICANTJOBPROTECT = #{applicantjobprotect, jdbcType=VARCHAR}

WHERE APPLICANTNO = #{applicantno}

mapper.delete:

	DELETE FROM TA_RECRUIT_APPLICANT
	WHERE APPLICANTNO = #{APPLICANTNO, JDBCTYPE= NUMERIC}
      AND RECRUITNO = #{APPLICANTNO, JDBCTYPE= NUMERIC}
