mapper.select:
select AP.applicantNo ,AP.applicantId ,AP.applyNo ,AP.applicantMillitary ,AP.applicantVeteran ,AP.applicantDisability ,AP.applicantJobProtect ,AP.applyDate ,AP.finalApplyChecked
from ta_recruit_applicant AP ,ta_recruit_notice N where 1=1 and AP.recruitNo=N.recruitNO and AP.recruitNo=#{RECRUITNO}
AND AP.RECRUITNO = #{RECRUITNO ,jdbcType=NUMERIC}

mapper.insert:
insert into ta_applicant
(
	  RECRUITNO
	, APPLICANTNO
	, APPLICANTID
	, FINALAPPLYCHECKED
	, APPLYNO
	, APPLICANTMILLITARY
	, APPLICANTVETERAN
	, APPLICANTDISABILITY
	, APPLICANTJOBPROTECT
	, APPLYDATE


)
values
(
	  #{RECRUITNO}
	, #{APPLICANTNO}
	, #{APPLICANTID}
	, #{FINALAPPLYCHECKED}
	, #{APPLYNO}
	, #{APPLICANTMILLITARY}
	, #{APPLICANTVETERAN}
	, #{APPLICANTDISABILITY}
	, #{APPLICANTJOBPROTECT}
	, SYSDATE()


)

mapper.update:
update ta_recruit_applicant
set   UPDATEDATE = SYSDATE();

	, FINALAPPLYCHECKED = #{FINALAPPLYCHECKED, jdbcType=NUMERIC}



	, APPLYNO = #{APPLYNO, jdbcType=VARCHAR}


	, APPLICANTMILLITARY = #{APPLICANTMILLITARY, jdbcType=VARCHAR}


	, APPLICANTVETERAN = #{APPLICANTVETERAN, jdbcType=VARCHAR}


	, APPLICANTDISABILITY = #{APPLICANTDISABILITY, jdbcType=VARCHAR}


	, APPLICANTJOBPROTECT = #{APPLICANTJOBPROTECT, jdbcType=VARCHAR}

WHERE APPLICANTNO = #{APPLICANTNO, JDBCTYPE= NUMERIC}
	  AND RECRUITNO = #{APPLICANTNO, JDBCTYPE= NUMERIC}

mapper.delete:

	DELETE FROM TA_RECRUIT_APPLICANT
	WHERE APPLICANTNO = #{APPLICANTNO, JDBCTYPE= NUMERIC}
      AND RECRUITNO = #{APPLICANTNO, JDBCTYPE= NUMERIC}
