mapper.select:
select AP.applyno , N.recruitno , P.partname , AP.partno , O.objectname , AP.objectno , W.workname , AP.workno , AP.applyContent , AP.applyUpdateDate
from ta_recruit_apply AP ,ta_recruit_notice N ,ta_recruit_part P ,ta_recruit_object O ,ta_recruit_work W where 1=1 and AP.recruitNo=N.recruitNO and AP.recruitNo=#{RECRUITNO} and AP.objectNo = O.objectNo and AP.partNo = P.partNo and AP.workNo = W.workNo
AND AP.RECRUITNO = #{RECRUITNO ,jdbcType=NUMERIC}

mapper.insert:
insert into ta_recruit_apply
(
	  APPLYNO
	, RECRUITNO
	, PARTNO
	, OBJECTNO
	, WORKNO
	, APPLYCONTENT

)
values
(
	  (SELECT IFNULL(MAX(applyno),1) FROM ta_recruit_apply A)+1
	, #{recruitno}
	, #{partno}
	, #{objectno}
	, #{workno}
	, #{applycontent}



)

mapper.update:
update ta_recruit_apply
set   APPLYUPDATEDATE = SYSDATE()

	, PARTNO =  #{partno, jdbcType=VARCHAR}




	, WORKNO =  #{workno, jdbcType=VARCHAR}



	, OBJECTNO = #{objectno, jdbcType=VARCHAR}




	, APPLYCONTENT = #{applycontent, jdbcType=VARCHAR}


WHERE ApplyNo = #{applyno}
