mapper.insert:
INSERT INTO TA_RECRUIT_BBS ( BBSNO , BBSTITLE , BBSCONTENT , BBSWRITEDATE , BBSHIT
) values ( (SELECT IFNULL(MAX(BBSNO),0) FROM TA_RECRUIT_BBS A)+1 , #{bbstitle} , #{bbscontent} , SYSDATE() , 0
)

mapper.update:
UPDATE TA_RECRUIT_BBS
SET BBSUPDATEDATE = SYSDATE()

	, BBSTITLE =  #{bbstitle, jdbcType=VARCHAR}


	, BBSCONTENT =  #{bbscontent, jdbcType=VARCHAR}

WHERE BBSNO = #{bbsno};
