mapper.insert:
        /*[TbbsCtegryDtl.xml][insert][TANINE]*/
        INSERT INTO TBBS_CTEGRY_DTL
		(
			 CTEGRY_MST_CD
		   , CTEGRY_DTL_CD
		   , CTEGRY_DTL_NM
		   , REF_VAL1
		   , REF_VAL2
		   , REF_VAL3
		   , SRT_SEQ
		   , US_YN
		   , CRTR_ID
		   , CRT_DTTM
		   , UPDR_ID
		   , UPD_DTTM
		)
		VALUES
		(
			 #{ctegry_mst_cd, jdbcType=VARCHAR}
		   , ( SELECT CTEGRY_DTL_CD FROM (SELECT IFNULL(MAX(CAST(CTEGRY_DTL_CD AS SIGNED INTEGER))+10,10) AS CTEGRY_DTL_CD FROM TBBS_CTEGRY_DTL WHERE CTEGRY_MST_CD = #{ctegry_mst_cd, jdbcType=VARCHAR} ) A )
		   , #{ctegry_dtl_nm, jdbcType=VARCHAR}
		   , #{ref_val1, jdbcType=VARCHAR}
		   , #{ref_val2, jdbcType=VARCHAR}
		   , #{ref_val3, jdbcType=VARCHAR}
		   , #{srt_seq, jdbcType=NUMERIC}
		   , #{us_yn, jdbcType=VARCHAR}
		   , #{crtr_id, jdbcType=VARCHAR}
		   , SYSDATE()
		   , #{updr_id, jdbcType=VARCHAR}
		   , SYSDATE()
		)

mapper.update:
        /*[TbbsCtegryDtl.xml][update][TANINE]*/
		UPDATE TBBS_CTEGRY_DTL
		   SET 
		       CTEGRY_DTL_NM = #{ctegry_dtl_nm, jdbcType=VARCHAR}


		     , REF_VAL1 = #{ref_val1, jdbcType=VARCHAR}


		     , REF_VAL2 = #{ref_val2, jdbcType=VARCHAR}


		     , REF_VAL3 = #{ref_val3, jdbcType=VARCHAR}


		     , SRT_SEQ = #{srt_seq, jdbcType=NUMERIC}


		     , US_YN = #{us_yn, jdbcType=VARCHAR}


		     , CRTR_ID = #{crtr_id, jdbcType=VARCHAR}


		     , CRT_DTTM = #{crt_dttm, jdbcType=TIMESTAMP}


		     , UPDR_ID = #{updr_id, jdbcType=VARCHAR}


		     , UPD_DTTM = #{upd_dttm, jdbcType=TIMESTAMP}

		WHERE CTEGRY_DTL_CD = #{ctegry_dtl_cd, jdbcType=VARCHAR}
           AND CTEGRY_MST_CD = #{ctegry_mst_cd, jdbcType=VARCHAR}

mapper.delete:
        /*[TbbsCtegryDtl.xml][delete][TANINE]*/
		DELETE FROM TBBS_CTEGRY_DTL
		 WHERE CTEGRY_DTL_CD = #{ctegry_dtl_cd, jdbcType=VARCHAR}
           AND CTEGRY_MST_CD = #{ctegry_mst_cd, jdbcType=VARCHAR}

mapper.select:
/* [TbbsCtegryDtl.xml][selectTbbsCtegryDtlList][코드 상세 리스트 조회 ][TANINE] */
SELECT CM.CTEGRY_MST_CD        -- 마스터코드
      ,CD.CTEGRY_DTL_CD        -- 상세코드
      ,CD.CTEGRY_DTL_NM        -- 상세코드명
      ,CD.REF_VAL1             -- 참조값1
      ,CD.REF_VAL2             -- 참조값2
      ,CD.REF_VAL3             -- 참조값3
      ,CD.SRT_SEQ	       -- 사이트번호
      ,CD.US_YN			   -- 사용여부
      ,CD.CRTR_ID			   -- 등록자아이디
      ,CD.CRT_DTTM		       -- 등록일시
      ,CD.UPDR_ID		       -- 수정자아이디
      ,CD.UPD_DTTM		       -- 수정일시
  FROM TBBS_CTEGRY_DTL CD
      ,TBBS_CTEGRY_MST CM
 WHERE CD.CTEGRY_MST_CD = CM.CTEGRY_MST_CD
   AND CM.CTEGRY_MST_CD            = #{ctegry_mst_cd        ,jdbcType=VARCHAR}
   AND CD.CTEGRY_DTL_CD                = #{ctegry_dtl_cd            ,jdbcType=VARCHAR}
   AND CD.REF_VAL1             = #{ref_val1         ,jdbcType=VARCHAR}
   AND CD.REF_VAL2             = #{ref_val2         ,jdbcType=VARCHAR}
   AND CD.REF_VAL3             = #{ref_val3         ,jdbcType=VARCHAR}

   AND (

       CD.REF_VAL1             = #{ref_val1         ,jdbcType=VARCHAR}



          OR

          CD.REF_VAL2             = #{ref_val2         ,jdbcType=VARCHAR}



          OR 

         CD.REF_VAL3             = #{ref_val3         ,jdbcType=VARCHAR}  

       )

   AND CD.US_YN            = #{us_yn        ,jdbcType=VARCHAR}

/*[TbbsCtegryDtl.xml]["selectTbbsCtegryDtlGridList"][코드 상세 그리드 리스트 조회][TANINE]*/ SELECT P.* FROM (SELECT Ceil(V.id / #{rows, jdbcType=NUMERIC}) PAGE , Ceil(@row_number / #{rows, jdbcType=NUMERIC}) TOTAL_PAGE , V.* , @row_number AS TOTAL_COUNT FROM (SELECT CDD.CTEGRY_MST_CD -- 마스터코드 , CDD.CTEGRY_DTL_CD -- 상세코드 , CDD.CTEGRY_DTL_NM -- 상세코드명 , CDD.REF_VAL1 -- 참조값1 , CDD.REF_VAL2 -- 참조값2 , CDD.REF_VAL3 -- 참조값3 , CDD.US_YN -- 사용여부 , CDD.SRT_SEQ -- 정렬순서 , CDD.CRTR_ID -- 등록자 , CDD.CRT_DTTM -- 등록일 , CDD.UPDR_ID -- 수정자 , CDD.UPD_DTTM -- 수정일 , DATE_FORMAT(CDD.UPD_DTTM, '%Y-%m-%d %H:%i:%s') AS F_UPD_DTTM , CDM.CTEGRY_MST_CD AS UP_CTEGRY_MST_CD -- 상위 마스터 코드 , IFNULL(SU.LGN_ID,'SYSTEM') AS REG_LGN_ID ,( @row_number := @row_number + 1 ) AS ID -- mysql ROWNUM FROM TBBS_CTEGRY_DTL CDD INNER JOIN TBBS_CTEGRY_MST CDM ON CDD.CTEGRY_MST_CD = CDM.CTEGRY_MST_CD LEFT OUTER JOIN TMBR_ADM_LGN_INF SU ON SU.USR_ID = CDD.CRTR_ID , (SELECT @row_number := 0) RN -- mysql ROWNUM WHERE 1 = 1
AND CDD.CTEGRY_MST_CD = #{ctegry_mst_cd ,jdbcType=VARCHAR} AND CDD.CTEGRY_DTL_CD = #{ctegry_dtl_cd ,jdbcType=NUMERIC} AND CDD.REF_VAL1 = #{ref_val1 ,jdbcType=VARCHAR} AND CDD.REF_VAL2 = #{ref_val2 ,jdbcType=VARCHAR} AND CDD.REF_VAL3 = #{ref_val3 ,jdbcType=VARCHAR} AND CDD.CTEGRY_DTL_NM LIKE CONCAT('%',#{sch_cd_nm ,jdbcType=VARCHAR},'%') AND CDD.US_YN = #{sch_us_yn ,jdbcType=VARCHAR} ORDER BY CDD.SRT_SEQ ) V )P
WHERE P.page = #{page ,jdbcType=NUMERIC}
ORDER BY P.SRT_SEQ

/*[TbbsCtegryDtl.xml][selectTbbsCtegryDtlCdNm][코드명 체크][TANINE]*/
    SELECT COUNT(1) 
      FROM TBBS_CTEGRY_DTL
     WHERE CTEGRY_MST_CD = #{ctegry_mst_cd ,jdbcType=VARCHAR}
       AND CTEGRY_DTL_NM =  #{ctegry_dtl_nm ,jdbcType=VARCHAR}
       AND US_YN = 'Y'
