mapper.insert:
        /*[TbbsCtegryMst.xml][insert][TANINE]*/
        INSERT INTO TBBS_CTEGRY_MST
		(
			 CTEGRY_MST_CD      /* 카테고리마스터코드 */
		   , CTEGRY_MST_NM      /* 카테고리마스터코드명 */
		   , CTEGRY_MST_DESC    /* 카테고리마스터코드설명 */
		   , US_YN             /* 사용여부 */
		   , SYS_CD            /* 사이트번호 */
		   , CRTR_ID            /* 등록자아이디 */
		   , CRT_DTTM           /* 등록일시 */
		   , UPDR_ID           /* 수정자아이디 */
		   , UPD_DTTM          /* 수정일시 */
		)
		VALUES
		(
			 #{ctegry_mst_cd, jdbcType=VARCHAR}
		   , #{ctegry_mst_nm, jdbcType=VARCHAR}
		   , #{ctegry_mst_desc, jdbcType=VARCHAR}
		   , #{us_yn, jdbcType=VARCHAR}
		   , #{sys_cd, jdbcType=VARCHAR}
		   , #{crtr_id, jdbcType=VARCHAR}
		   , SYSDATE()
		   , #{updr_id, jdbcType=VARCHAR}
		   , SYSDATE()
		)

mapper.update:
        /*[TbbsCtegryMst.xml][update][TANINE]*/
		UPDATE TBBS_CTEGRY_MST
		   SET 
		       CTEGRY_MST_NM = #{ctegry_mst_nm, jdbcType=VARCHAR}


		     , CTEGRY_MST_DESC = #{ctegry_mst_desc, jdbcType=VARCHAR}


		     , US_YN = #{us_yn, jdbcType=VARCHAR}


		     , SYS_CD = #{sys_cd, jdbcType=VARCHAR}


		     , CRTR_ID = #{crtr_id, jdbcType=VARCHAR}


		     , CRT_DTTM = #{crt_dttm, jdbcType=TIMESTAMP}


		     , UPDR_ID = #{updr_id, jdbcType=VARCHAR}


		     , UPD_DTTM = #{upd_dttm, jdbcType=TIMESTAMP}

		WHERE CTEGRY_MST_CD = #{ctegry_mst_cd, jdbcType=VARCHAR}

mapper.delete:
        /*[TbbsCtegryMst.xml][delete][TANINE]*/
		DELETE FROM TBBS_CTEGRY_MST
		 WHERE CTEGRY_MST_CD = #{ctegry_mst_cd, jdbcType=VARCHAR}

mapper.select:
/*[TbbsCtegryMst.xml][selectTbbsCtegryMstGridList][마스터 코드 그리드 리스트 조회][TANINE]*/ SELECT P.* FROM (SELECT CEIL(V.ID / #{rows, jdbcType=NUMERIC}) AS PAGE /* 페이지 번호 */ , CEIL(@ROW_NUMBER / #{rows, jdbcType=NUMERIC}) AS TOTAL_PAGE /* 전체 페이지 */ , @ROW_NUMBER AS TOTAL_COUNT /* 전제 ROW수 */ , V.* FROM (SELECT CM.CTEGRY_MST_CD AS CTEGRY_MST_CD /* 마스터코드 */ , CM.CTEGRY_MST_NM AS CTEGRY_MST_NM /* 마스터코드명 */ , CM.CTEGRY_MST_DESC AS CTEGRY_MST_DESC /* 마스터코드설명 */ , CM.US_YN AS US_YN /* 사용여부 */ , CM.UPD_DTTM AS UPD_DTTM /* 수정일시 */ , DATE_FORMAT(CM.UPD_DTTM, '%Y-%m-%d %H:%i:%s') AS F_UPD_DTTM /* 수정일자 */ ,( @row_number := @row_number + 1 ) AS ID /* MYSQL ROWNUM */ , CM.SYS_CD AS SYS_CD FROM TBBS_CTEGRY_MST CM /* 마스터코드 */ , (SELECT @ROW_NUMBER := 0) RN /* MYSQL ROWNUM */ WHERE 1 = 1
AND CM.CTEGRY_MST_CD = #{ctegry_mst_cd, jdbcType=VARCHAR}
AND CM.CTEGRY_MST_NM LIKE CONCAT('%',#{ctegry_mst_nm, jdbcType=VARCHAR}, '%')
) V ) P
WHERE P.PAGE = #{page, jdbcType=NUMERIC}
ORDER BY P.CTEGRY_MST_CD

/*[TbbsCtegryMst.xml][excelTbbsCtegryMstGridList][엑셀다운로드][TANINE]*/ SELECT P.* FROM (SELECT CEIL(V.ID / #{rows, jdbcType=NUMERIC}) AS PAGE /* 페이지 번호 */ , CEIL(@ROW_NUMBER / #{rows, jdbcType=NUMERIC}) AS TOTAL_PAGE /* 전체 페이지 */ , @ROW_NUMBER AS TOTAL_COUNT /* 전제 ROW수 */ , V.* FROM (SELECT CM.CTEGRY_MST_CD AS CTEGRY_MST_CD /* 마스터코드 */ , CM.CTEGRY_MST_NM AS CTEGRY_MST_NM /* 마스터코드명 */ , CM.CTEGRY_MST_DESC AS CTEGRY_MST_DESC /* 마스터코드설명 */ , CM.US_YN AS US_YN /* 사용여부 */ , CM.UPD_DTTM AS UPD_DTTM /* 수정일시 */ , DATE_FORMAT(CM.UPD_DTTM, '%Y-%m-%d %H:%i:%s') AS F_UPD_DTTM /* 수정일자 */ ,( @row_number := @row_number + 1 ) AS ID /* MYSQL ROWNUM */ , (SELECT SYS_NM FROM TDSP_SYS_INF WHERE SYS_CD = CM.SYS_CD) AS SYS_NM FROM TBBS_CTEGRY_MST CM /* 마스터코드 */ , (SELECT @ROW_NUMBER := 0) RN /* MYSQL ROWNUM */ WHERE 1 = 1
AND CM.CTEGRY_MST_CD = #{ctegry_mst_cd, jdbcType=VARCHAR}
AND CM.CTEGRY_MST_NM LIKE CONCAT('%',#{ctegry_mst_nm, jdbcType=VARCHAR}, '%')
) V ) P
WHERE P.PAGE = #{page, jdbcType=NUMERIC}
ORDER BY P.CTEGRY_MST_CD

mapper.delete:
		/* [TcmnCdmstTrx.xml][deleteTcmnCdDtl][마스터 코드 / 상세 코드 삭제][TANINE] */
		DELETE
          FROM TBBS_CTEGRY_DTL
         WHERE CTEGRY_MST_CD = #{ctegry_mst_cd, jdbcType=VARCHAR}
