mapper.sql:
		/*[TbbsDocUsrRtnn.xml][select][TANINE]*/
		SELECT USR_ID
             , MODL_SEQ
             , DOC_SEQ
             , CRTR_ID
             , CRT_DTTM
             , UPDR_ID
             , UPD_DTTM
          FROM TBBS_DOC_USR_RTNN
		 WHERE DOC_SEQ = #{doc_seq, jdbcType=NUMERIC} 
           AND MODL_SEQ = #{modl_seq, jdbcType=NUMERIC} 
           AND USR_ID = #{usr_id, jdbcType=VARCHAR}
