mapper.insert:
        /*[BaseTcmnCdMstTrx.xml][insert][시스템 코드 등록][TANINE]*/
        INSERT INTO TCMN_CD_MST
		(
			 MST_CD         /* 마스터코드 */
		   , MST_CD_NM      /* 마스터코드명 */
		   , MST_CD_DESC    /* 마스터코드설명 */
		   , SRT_SEQ        /* 정렬순서(전시순서) */
		   , US_YN          /* 사용여부 */
		   , CRTR_ID        /* 생성자ID */
		   , CRT_DTTM       /* 생성일시 */
		   , UPDR_ID        /* 수정자ID */
		   , UPD_DTTM       /* 수정일시 */
		)
		VALUES
		(
			 UPPER(#{mst_cd, jdbcType=VARCHAR})
		   , #{mst_cd_nm,    jdbcType=VARCHAR}
		   , #{mst_cd_desc,  jdbcType=LONGVARCHAR}
		   , #{srt_seq,      jdbcType=NUMERIC}
		   , #{us_yn,        jdbcType=VARCHAR}
		   , #{crtr_id,      jdbcType=VARCHAR}
		   , SYSDATE()
		   , #{updr_id,      jdbcType=VARCHAR}
		   , SYSDATE()
		)

mapper.update:
        /*[BaseTcmnCdMstTrx.xml][update][시스템 코드 마스터 수정][TANINE]*/
		UPDATE TCMN_CD_MST
		   SET UPD_DTTM = SYSDATE() 

		     , MST_CD_NM = #{mst_cd_nm, jdbcType=VARCHAR}


		     , MST_CD_DESC = #{mst_cd_desc, jdbcType=LONGVARCHAR}


		     , SRT_SEQ = #{srt_seq, jdbcType=NUMERIC}


		     , US_YN = #{us_yn, jdbcType=VARCHAR}


		     , UPDR_ID = #{updr_id, jdbcType=VARCHAR}

		WHERE MST_CD = #{mst_cd, jdbcType=VARCHAR}

mapper.delete:
        /*[BaseTcmnCdMst.xml][delete][시스템 코드 마스터 삭제][TANINE]*/
		DELETE FROM TCMN_CD_MST
		 WHERE MST_CD = #{mst_cd, jdbcType=VARCHAR}

mapper.select:
/*[TcmnCdMst.xml][selectTcmnCdMstGridList][마스터 코드 그리드 리스트 조회][TANINE]*/ SELECT P.* FROM (SELECT CEIL(V.ID / #{rows, jdbcType=NUMERIC}) AS PAGE /* 페이지 번호 */ , CEIL(@ROW_NUMBER / #{rows, jdbcType=NUMERIC}) AS TOTAL_PAGE /* 전체 페이지 */ , @ROW_NUMBER AS TOTAL_COUNT /* 전제 ROW수 */ , V.* FROM (SELECT CM.MST_CD AS MST_CD /* 마스터코드 */ , CM.MST_CD_NM AS MST_CD_NM /* 마스터코드명 */ , CM.MST_CD_DESC AS MST_CD_DESC /* 마스터코드설명 */ , CM.US_YN AS US_YN /* 사용여부 */ , CM.UPD_DTTM AS UPD_DTTM /* 수정일시 */ , DATE_FORMAT(CM.UPD_DTTM, '%Y-%m-%d %H:%i:%s') AS F_UPD_DTTM /* 수정일자 */ ,( @row_number := @row_number + 1 ) AS ID /* MYSQL ROWNUM */ FROM TCMN_CD_MST CM /* 마스터코드 */ , (SELECT @ROW_NUMBER := 0) RN /* MYSQL ROWNUM */ WHERE 1 = 1
AND CM.MST_CD = #{mst_cd, jdbcType=VARCHAR}
AND CM.MST_CD_NM LIKE CONCAT('%',#{mst_cd_nm, jdbcType=VARCHAR}, '%')
) V
ORDER BY V.MST_CD
) P
WHERE P.PAGE = #{page, jdbcType=NUMERIC}

mapper.delete:
		/* [TcmnCdMstTrx.xml][deleteTcmnCdDtl][마스터 코드 / 상세 코드 삭제][TANINE] */
		DELETE
          FROM TCMN_CD_DTL
         WHERE MST_CD = #{mst_cd, jdbcType=VARCHAR}
