mapper.insert:
        /*[TdspSysInf.xml][insert][사이트 등록][TANINE]*/
        INSERT INTO TDSP_SYS_INF
		(
		     SYS_NM                     /* 사이트명 */
		   , SYS_PTH                    /* 사이트경로 */
		   , SYS_URL					/* 사이트URL */
		   , US_YN                      /* 사용여부 */
		   , SRT_SEQ					/* 정렬순서 */
           , DSPLY_YN                    /*권한사용여부*/
           , SYS_ABRV                   /*약어명*/
           , SYS_DESC              /* 사이트 설명 내용 */ 
		   , CRTR_ID                    /* 생성자ID */
		   , CRT_DTTM                   /* 생성일시 */
		   , UPDR_ID                    /* 수정자ID */
		   , UPD_DTTM                   /* 수정일시 */
		)
		VALUES
		(
		     #{sys_nm, jdbcType=VARCHAR}
		   , #{sys_pth, jdbcType=VARCHAR}
		   , #{sys_url, jdbcType=VARCHAR}
		   , #{us_yn, jdbcType=VARCHAR}
		   , #{srt_seq, jdbcType=NUMERIC}
		   , #{dsply_yn, jdbcType=VARCHAR}
		   , #{sys_abrv, jdbcType=VARCHAR}
		   , #{sys_desc, jdbcType=VARCHAR}
		   , #{crtr_id, jdbcType=VARCHAR}
		   , SYSDATE()
		   , #{updr_id, jdbcType=VARCHAR}
		   , SYSDATE()
		)

mapper.update:
         /* [BaseTdspSysInfTrx.xml][updateSite][사이트 수정][TANINE] */
		UPDATE TDSP_SYS_INF
		   SET UPDR_ID = #{updr_id, jdbcType=VARCHAR}
		     , UPD_DTTM = SYSDATE()

		     , SYS_NM = #{sys_nm, jdbcType=VARCHAR}


		     , SYS_PTH = #{sys_pth, jdbcType=VARCHAR}


		     , SYS_URL = #{sys_url, jdbcType=VARCHAR}


		     , US_YN = #{us_yn, jdbcType=VARCHAR}


		     , SRT_SEQ = #{srt_seq, jdbcType=VARCHAR}


		     , DSPLY_YN = #{dsply_yn, jdbcType=VARCHAR}


		     , SYS_ABRV = #{sys_abrv, jdbcType=VARCHAR}


		     , SYS_DESC = #{sys_desc, jdbcType=VARCHAR}

		 WHERE SYS_CD = #{sys_cd, jdbcType=VARCHAR}

mapper.delete:
        /* [BaseTdspSysInfTrx.xml][deleteSite][사이트 삭제][TANINE] */
		DELETE FROM TDSP_SYS_INF
		 WHERE SYS_CD = #{sys_cd, jdbcType=VARCHAR}

mapper.select:
/* [TdspSysInf.xml][selectTdspSysInfList][사이트 조회][TANINE] */ SELECT P.* FROM ( SELECT CEIL(V2.ID / #{rows, jdbcType=NUMERIC}) AS PAGE , CEIL(@row_number / #{rows, jdbcType=NUMERIC}) AS TOTAL_PAGE , @row_number AS TOTAL_COUNT , V2.* FROM ( SELECT V1.* , ( @row_number := @row_number + 1 ) AS ID /* mysql ROWNUM */ FROM ( SELECT SYS_CD AS SYS_CD /* 사이트순번 */ , SYS_NM AS SYS_NM /* 사이트명 */ , SYS_PTH AS SYS_PTH /* 사이트경로 */ , SYS_URL AS SYS_URL /* 사이트URL */ , US_YN AS US_YN /* 사용여부 */ , SRT_SEQ AS SRT_SEQ /* 정렬순서 */ , DSPLY_YN AS DSPLY_YN /*권한사용여부*/ , SYS_ABRV AS SYS_ABRV /*약어명*/ , SYS_DESC AS SYS_DESC /* 사이트 설명 내용 */ , CRTR_ID AS CRTR_ID /* 생성자ID */ , CRT_DTTM AS CRT_DTTM /* 생성일시 */ , DATE_FORMAT(CRT_DTTM, '%Y-%m-%d %H:%i:%s') AS F_CRT_DTTM /* 작성일시 */ , UPDR_ID AS UPDR_ID /* 수정자ID */ , UPD_DTTM AS UPD_DTTM /* 수정일시 */ FROM TDSP_SYS_INF ORDER BY SRT_SEQ ) V1 , (SELECT @row_number := 0) RN /* mysql ROWNUM */ WHERE 1 = 1 AND SYS_CD !=5 -- 파트너포탈 검색 제외 ) V2 ) P
WHERE P.page = #{page ,jdbcType=NUMERIC}
ORDER BY P.SRT_SEQ, P.SYS_CD

/* [TdspSysInf.xml][selectTdspSysInfComboList][사이트 콤보 조회][TANINE] */ SELECT SYS_CD AS SYS_CD /* 사이트순번 */ , SYS_NM AS SYS_NM /* 사이트명 */ , SYS_PTH AS SYS_PTH /* 사이트경로 */ , SYS_URL AS SYS_URL /* 사이트URL */ , US_YN AS US_YN /* 사용여부 */ , SRT_SEQ AS SRT_SEQ /* 정렬순서 */ , DSPLY_YN AS DSPLY_YN /*권한사용여부*/ , SYS_ABRV AS SYS_ABRV /*약어명*/ , SYS_DESC AS SYS_DESC /* 사이트 설명 내용 */ , CRTR_ID AS CRTR_ID /* 생성자ID */ , CRT_DTTM AS CRT_DTTM /* 생성일시 */ , UPDR_ID AS UPDR_ID /* 수정자ID */ , UPD_DTTM AS UPD_DTTM /* 수정일시 */ FROM TDSP_SYS_INF WHERE US_YN = 'Y'
AND SYS_CD = #{sys_cd, jdbcType=VARCHAR}
AND DSPLY_YN = #{dsply_yn, jdbcType=VARCHAR}
ORDER BY SRT_SEQ

/* [TdspSysInf.xml][selectTdspSysInfLogComboList][로그인 이력 적재 가능 사이트 조회][TANINE] */ SELECT A.SYS_CD AS SYS_CD /* 사이트순번 */ , A.SYS_NM AS SYS_NM /* 사이트명 */ , A.SYS_PTH AS SYS_PTH /* 사이트경로 */ , A.SYS_URL AS SYS_URL /* 사이트URL */ , A.US_YN AS US_YN /* 사용여부 */ , A.SRT_SEQ AS SRT_SEQ /* 정렬순서 */ , A.DSPLY_YN AS DSPLY_YN /*권한사용여부*/ , A.SYS_ABRV AS SYS_ABRV /*약어명*/ , A.SYS_DESC AS SYS_DESC /* 사이트 설명 내용 */ , A.CRTR_ID AS CRTR_ID /* 생성자ID */ , A.CRT_DTTM AS CRT_DTTM /* 생성일시 */ , A.UPDR_ID AS UPDR_ID /* 수정자ID */ , A.UPD_DTTM AS UPD_DTTM /* 수정일시 */ FROM TDSP_SYS_INF A , TCMN_CD_MST B , TCMN_CD_DTL C WHERE A.US_YN = 'Y' AND B.US_YN = 'Y' AND B.MST_CD = 'SIT_LGN_LOG' AND B.MST_CD = C.MST_CD AND C.US_YN = 'Y' AND A.SYS_CD = C.REF_1
AND A.SYS_CD = #{sys_cd, jdbcType=VARCHAR}
AND A.DSPLY_YN = #{dsply_yn, jdbcType=VARCHAR}
AND C.REF_2 = #{ref_2, jdbcType=VARCHAR}
ORDER BY A.SRT_SEQ

SELECT SYS_CD AS SYS_CD /* 사이트순번 */ , SYS_NM AS SYS_NM /* 사이트명 */ , SYS_PTH AS SYS_PTH /* 사이트경로 */ , SYS_URL AS SYS_URL /* 사이트URL */ , US_YN AS US_YN /* 사용여부 */ , SRT_SEQ AS SRT_SEQ /* 정렬순서 */ , SYS_ABRV AS SYS_ABRV /* 사이트 약어명 */ , SYS_DESC AS SYS_DESC /* 사이트 설명 내용 */ , CRTR_ID AS CRTR_ID /* 생성자ID */ , CRT_DTTM AS CRT_DTTM /* 생성일시 */ , UPDR_ID AS UPDR_ID /* 수정자ID */ , UPD_DTTM AS UPD_DTTM /* 수정일시 */ FROM TDSP_SYS_INF WHERE US_YN = 'Y'
AND DSPLY_YN = #{dsply_yn, jdbcType=NUMERIC}
ORDER BY SRT_SEQ
