/*[BaseTdspTmplInf.xml][select][템플릿 조회][TANINE]*/ SELECT TMPL_SEQ , UP_TMPL_SEQ , TMPL_NM , TMPL_TP_CD , TMPL_DTL_NM , TMPL_URL , DSPLY_NO , DSPLY_YN , MNU_SEQ , MODL_SEQ , MN_CTS_DESC , LGN_ESN_YN , US_YN , CRTR_ID , DATE_FORMAT(CRT_DTTM, '%Y%m%d%H%i%s')
, UPDR_ID , DATE_FORMAT(UPD_DTTM, '%Y%m%d%H%i%s')
FROM TDSP_TMPL_INF WHERE TMPL_SEQ = #{tmpl_seq, jdbcType=NUMERIC} UP_TMPL_SEQ = #{up_tmpl_seq, jdbcType=VARCHAR}

