mapper.select:
/* [TdspTmplInf.xml][selectTdspTmplInfGridList][템플릿 리스트 조회(그리드형)][TANINE] */ SELECT P.* FROM ( SELECT CEIL(V2.ID / #{rows, jdbcType=NUMERIC}) AS PAGE , CEIL(@row_number / #{rows, jdbcType=NUMERIC}) AS TOTAL_PAGE , @row_number AS TOTAL_COUNT , V2.* FROM ( SELECT V1.* , ( @row_number := @row_number + 1 ) AS ID /* mysql ROWNUM */ FROM ( SELECT TT.CD_NM AS TMPL_TP_CD_NM -- 템플릿유형코드명 , FM.MNU_NM AS MNU_NM -- 메뉴명 , ST.MNU_SEQ AS MNU_SEQ -- 메뉴순번 , ST.TMPL_SEQ AS TMPL_SEQ -- 템플릿순번 , ST.UP_TMPL_SEQ AS UP_TMPL_SEQ -- 상위템플릿순번 , ST.TMPL_NM AS TMPL_NM -- 템플릿명 , ST.TMPL_TP_CD AS TMPL_TP_CD -- 템플릿유형코드 , ST.TMPL_DTL_NM AS TMPL_DTL_NM -- 템플릿상세명 , ST.TMPL_URL AS TMPL_URL -- 템플릿URL , ST.DSPLY_NO AS DSPLY_NO -- 전시번호 , ST.MN_CTS_DESC AS MN_CTS_DESC -- 메인컨텐츠설명 , ST.US_YN AS US_YN -- 사용여부 , ST.CRTR_ID AS CRTR_ID -- 생성자ID , ST.CRT_DTTM AS CRT_DTTM -- 생성일시 , ST.UPDR_ID AS UPDR_ID -- 수정자ID , ST.UPD_DTTM AS UPD_DTTM -- 수정일시 , UST.TMPL_NM AS UP_TMPL_NM -- 상위 템플릿명 FROM TDSP_TMPL_INF ST LEFT JOIN TDSP_TMPL_INF UST ON ST.UP_TMPL_SEQ = UST.TMPL_SEQ LEFT JOIN (SELECT MNU_NM, MNU_SEQ FROM TSYS_ADM_MNU WHERE FRNT_YN = 'Y') FM ON FM.MNU_SEQ = ST.MNU_SEQ LEFT JOIN (SELECT CD.CD_NM, CD.CD FROM TCMN_CD_DTL CD WHERE CD.MST_CD = 'TMP_TYPE') TT ON TT.CD = ST.TMPL_TP_CD WHERE 1 = 1 AND ST.TMPL_TP_CD = #{tmpl_tp_cd, jdbcType=VARCHAR} AND ST.US_YN = #{us_yn, jdbcType=VARCHAR} AND ST.TMPL_NM LIKE CONCAT('%',#{tmpl_nm ,jdbcType=VARCHAR},'%') ) V1 , (SELECT @row_number := 0) RN /* mysql ROWNUM */ WHERE 1 = 1 AND V1.MNU_NM LIKE CONCAT('%',#{mnu_nm ,jdbcType=VARCHAR},'%')
ORDER BY CONVERT(V1.TMPL_SEQ, unsigned)
) V2 )P
WHERE P.page = #{page ,jdbcType=NUMERIC}

/* [TdspTmplInf.xml][selectTdspTmplInfList][ 템플릿 리스트 조회(매핑여부/탬플릿유형별)][TANINE] */ SELECT T.* FROM ( SELECT ST.MNU_SEQ AS MNU_SEQ , ST.TMPL_SEQ AS TMPL_SEQ , ST.UP_TMPL_SEQ AS UP_TMPL_SEQ , ST.TMPL_NM AS TMPL_NM , ST.TMPL_TP_CD AS TMPL_TP_CD , ST.TMPL_URL AS TMPL_URL , ST.DSPLY_NO AS DSPLY_NO , ST.US_YN AS US_YN , CASE WHEN TMPL_TP_CD = '1' AND MNU_SEQ IS NULL THEN 'N' ELSE CASE WHEN MNU_SEQ IS NULL AND UP_TMPL_SEQ IS NULL THEN 'N' ELSE 'Y' END END AS MAPPED_YN , (SELECT s1.MNU_NM FROM TSYS_ADM_MNU s1 WHERE s1.MNU_SEQ = ST.MNU_SEQ) AS MNU_NM FROM TDSP_TMPL_INF ST WHERE ST.US_YN = 'Y'
AND ST.TMPL_TP_CD IN (SELECT CD FROM TCMN_CD_DTL WHERE MST_CD = 'TMP_TYPE' AND REF_2 = 'N' AND REF_3 = 'N')
AND ST.TMPL_TP_CD IN (SELECT CD FROM TCMN_CD_DTL WHERE MST_CD = 'TMP_TYPE' AND REF_2 = 'N' AND REF_3 = 'Y')
) T WHERE 1=1 AND T.MAPPED_YN = #{mapped_yn ,jdbcType=VARCHAR} ORDER BY T.MNU_NM

/* [TdspTmplInf.xml][selectTdspTmplInfDwnDspMnuGridList][ 전시메뉴(하위) 리스트 조회(그리드형)][TANINE] */ SELECT P.* FROM ( SELECT CEIL(V2.id / #{rows, jdbcType=NUMERIC}) AS PAGE , CEIL(@row_number / #{rows, jdbcType=NUMERIC}) AS TOTAL_PAGE , @row_number AS TOTAL_COUNT , V2.* FROM ( SELECT V1.* , ( @row_number := @row_number + 1 ) AS ID /* mysql ROWNUM */ FROM ( SELECT A.TMPL_SEQ AS TMPL_SEQ , A.UP_TMPL_SEQ AS UP_TMPL_SEQ , A.MNU_SEQ AS MNU_SEQ , A.TMPL_TP_CD AS TMPL_TP_CD , (SELECT CD_NM FROM TCMN_CD_DTL WHERE MST_CD = 'TMP_TYPE' AND CD = A.TMPL_TP_CD) AS TMPL_TP_CD_NM , A.TMPL_NM AS TMPL_NM , A.DSPLY_NO AS DSPLY_NO , A.DSPLY_YN AS DSPLY_YN , B.URL AS TMPL_URL , B.MNU_NM AS MNU_NM , B.MNU_YN AS MNU_YN FROM TDSP_TMPL_INF A LEFT JOIN TSYS_ADM_MNU B ON (A.MNU_SEQ = B.MNU_SEQ) WHERE A.UP_TMPL_SEQ = #{up_tmpl_seq ,jdbcType=NUMERIC} AND A.MNU_SEQ IS NOT NULL ) V1 , (SELECT @row_number := 0) RN /* mysql ROWNUM */ WHERE 1 = 1
ORDER BY V1.DSPLY_NO
) V2 )P
WHERE P.page = #{page ,jdbcType=NUMERIC}

mapper.update:
/* [TdspTmplInf.xml][updateTdspTmplInfMpnMnu][템플릿 매핑 초기화 ][TANINE] */
UPDATE TDSP_TMPL_INF A
   SET A.UPD_DTTM = SYSDATE() 
     , A.UPDR_ID = #{updr_id, jdbcType=VARCHAR} 
     , A.MNU_SEQ = NULL 
     , A.UP_TMPL_SEQ = NULL 
 WHERE 1=1
   AND A.TMPL_SEQ != #{tmpl_seq, jdbcType=NUMERIC}  
   AND A.MNU_SEQ = #{mnu_seq, jdbcType=NUMERIC}

