mapper.select:
/* [TdspTmplInf.xml][selectFrntMnuList][프론트 메뉴 목록 조회][TANINE] */ SELECT T1.DSPLY_NO , T1.TMPL_SEQ , T1.UP_TMPL_SEQ , T2.MNU_NM , CAST(T3.REF_1 AS UNSIGNED) AS MNU_DPTH , T2.URL FROM TDSP_TMPL_INF T1 LEFT JOIN (SELECT MNU_SEQ , MNU_NM , URL , SYS_CD FROM TSYS_ADM_MNU WHERE FRNT_YN = 'Y' AND US_YN = 'Y') T2 ON T1.MNU_SEQ = T2.MNU_SEQ LEFT JOIN (SELECT CD , REF_1 FROM TCMN_CD_DTL WHERE MST_CD = 'TMP_TYPE' AND US_YN = 'Y') T3 ON T1.TMPL_TP_CD = T3.CD WHERE 1 = 1 AND T1.US_YN = 'Y' AND T1.DSPLY_YN = 'Y' AND T1.MNU_SEQ != '' AND T1.MNU_SEQ IS NOT NULL AND IFNULL(T1.MNU_SEQ, '') != '' AND (CASE WHEN T1.TMPL_TP_CD = 1 THEN 'Y' ELSE (SELECT IFNULL(S1.DSPLY_YN, 'N') FROM TDSP_TMPL_INF S1 WHERE S1.TMPL_SEQ = T1.UP_TMPL_SEQ) END) = 'Y' AND T3.REF_1 > 0 AND T2.SYS_CD = #{sys_cd, jdbcType=NUMERIC}
AND T1.UP_TMPL_SEQ = #{up_tmpl_seq, jdbcType=NUMERIC}
ORDER BY CAST(T3.REF_1 AS UNSIGNED) , T1.DSPLY_NO , T1.UP_TMPL_SEQ , T1.TMPL_SEQ
