mapper.select:
/*[TmbrUsrInf.xml][selectTmbrUsrInfGridList][사용자 그리드 리스트 조회][TANINE]*/ SELECT P.* FROM ( SELECT V.*
, CEIL(V.ID / #{rows, jdbcType=NUMERIC}) PAGE , @ROW_NUMBER TOTAL_COUNT , CEIL(@ROW_NUMBER / #{rows, jdbcType=NUMERIC}) TOTAL_PAGE
FROM ( SELECT Y.* , (@ROW_NUMBER := @ROW_NUMBER + 1) ID FROM ( SELECT RPAD(SUBSTR(SU.USR_NM, 1, 1), 3, '*') AS USR_NM , SU.LGN_ID , CONCAT(RPAD(SUBSTR(SU.USR_EML_ADR, 1, 2), 6, '*'), SUBSTR(SU.USR_EML_ADR,INSTR(SU.USR_EML_ADR, '@'), LENGTH(SU.USR_EML_ADR))) AS USR_EML_ADR , SU.USR_SS_CD , SU.USR_AUTH_TP_CD , SU.ACTV_YN , SU.CRT_DTTM , SU.USR_ID , CONCAT(USR_TEL_NO_1, '-', LPAD("*", 4, '*'), '-', USR_TEL_NO_3) AS USR_TEL_NO , CONCAT(USR_MBL_NO_1, '-', LPAD('*', 4, '*'), '-', USR_MBL_NO_3) AS USR_MBL_NO , CASE WHEN SU.USR_SS_CD = '2' THEN (SELECT DATE_FORMAT(MAX(CRT_DTTM), '%Y-%M-%D') FROM TMBR_USR_SCSN_INF WHERE LGN_ID = SU.LGN_ID) ELSE NULL END SCSN_DTTM FROM TMBR_USR_INF SU WHERE 1 = 1 AND SU.USR_EML_ADR LIKE CONCAT('%', #{usr_eml_adr, jdbcType=VARCHAR}, '%') AND SU.LGN_ID LIKE CONCAT('%', #{lgn_id, jdbcType=VARCHAR}, '%') AND SU.USR_NM LIKE CONCAT('%', #{usr_nm, jdbcType=VARCHAR}, '%') AND SU.ACTV_YN = #{actv_yn, jdbcType=VARCHAR} AND SU.USR_AUTH_TP_CD = #{usr_auth_tp_cd, jdbcType=VARCHAR} AND SU.USR_SS_CD = #{usr_ss_cd, jdbcType=VARCHAR} AND DATE_FORMAT(SU.CRT_DTTM, '%Y-%m-%d') >= #{sch_st_dt1, jdbcType=VARCHAR} AND DATE_FORMAT(SU.CRT_DTTM, '%Y-%m-%d') <= #{sch_ed_dt1, jdbcType=VARCHAR} ) Y , (SELECT @row_number := 0) RN ) V WHERE 1 = 1 AND DATE_FORMAT(V.SCSN_DTTM, '%Y-%m-%d') >= #{sch_st_dt2, jdbcType=VARCHAR} AND DATE_FORMAT(V.SCSN_DTTM, '%Y-%m-%d') <= #{sch_ed_dt2, jdbcType=VARCHAR}
ORDER BY V.${sidx} ${sord}
ORDER BY V.USR_NM
) P
WHERE P.PAGE = #{page ,jdbcType=NUMERIC}

/*[TmbrUsrInf.xml][selectTmbrUsrInfChkEmlDpl][이메일 중복 확인][TANINE]*/
SELECT IF(COUNT(1) = 0, "Y", "N")
  FROM TMBR_USR_INF
 WHERE USR_EML_ADR = #{usr_eml_adr, jdbcType=VARCHAR}

		/*[TmbrUsrInf.xml][selectTmbrUsrInfChkChgCtfEml][이메일 변경 시 인증여부 확인][TANINE]*/
		SELECT CASE WHEN COUNT(1) > 0 THEN T.EML_TP_CD
		            ELSE '03'
                END AS EML_TP_CD
		  FROM (
			    SELECT CASE WHEN TMP_EML_ADR = USR_EML_ADR THEN '01'
			                WHEN TMP_EML_ADR IS NOT NULL AND TMP_EML_ADR != USR_EML_ADR THEN '02'
						    ELSE '03'
                        END EML_TP_CD
			      FROM TMBR_USR_INF
			     WHERE USR_EML_ADR = #{usr_eml_adr, jdbcType=VARCHAR}
			       AND LGN_ID = #{lgn_id, jdbcType=VARCHAR}
		       ) T

/*[TmbrUsrInf.xml][selectTmbrUsrInfChkEmail][이메일 유효성 확인][TANINE]*/
SELECT COUNT(1)
  FROM TMBR_USR_INF
 WHERE USR_EML_ADR = #{usr_eml_adr ,jdbcType=VARCHAR}

		/*[TmbrUsrInf.xml][selectTmbrUsrInfDtl][회원정보관리 회원 상세 조회][TANINE]*/
		SELECT USR_ID
             , USR_NM
             , LGN_ID
             , USR_EML_ADR
             , PWD_QST
             , PWD_ASW
             , EML_RCV_YN
             , USR_AUTH_TP_CD
             , USR_SS_CD
             , ACTV_YN
             , USR_DTL_INF
		  FROM TMBR_USR_INF SU
		 WHERE 1 = 1
		  AND EMAIL = #{email ,jdbcType=VARCHAR}
		 AND LGN_ID = #{lgn_id, jdbcType=VARCHAR}
		 AND USR_NM LIKE CONCAT('%', #{usr_nm, jdbcType=VARCHAR}, '%')
		 AND ACTV_YN = #{actv_yn, jdbcType=VARCHAR}
		 AND USR_AUTH_TP_CD = #{usr_auth_tp_cd, jdbcType=VARCHAR}
		 AND USR_SS_CD = #{usr_ss_cd, jdbcType=VARCHAR}
		 AND DATE_FORMAT(CRT_DTTM, '%Y-%m-%d') >= #{sch_st_dt, jdbcType=VARCHAR}
		 AND DATE_FORMAT(CRT_DTTM, '%Y-%m-%d') <= #{sch_ed_dt, jdbcType=VARCHAR}

mapper.update:
        /*[BaseTmbrUsrInf.xml][updateTmbrUsrInfMyPageInfo][마이페이지 : 사용자 수정][TANINE]*/
        UPDATE TMBR_USR_INF
           SET UPDR_ID = #{updr_id, jdbcType=VARCHAR}
		     , UPD_DTTM = SYSDATE()
             , USR_NM = #{usr_nm, jdbcType=VARCHAR}
             , PWD_QST = #{pwd_qst, jdbcType=VARCHAR}
             , PWD_ASW = #{pwd_asw, jdbcType=VARCHAR}

             , EML_RCV_YN = #{eml_rcv_yn, jdbcType=VARCHAR}

         WHERE LGN_ID = #{lgn_id, jdbcType=VARCHAR} 
           AND USR_ID = #{usr_id, jdbcType=VARCHAR}

        /*[BaseTmbrUsrInf.xml][updateTmbrUsrInfPwChg][사용자 비밀번호 변경][TANINE]*/
        UPDATE TMBR_USR_INF
           SET UPDR_ID = #{updr_id, jdbcType=VARCHAR}
		     , UPD_DTTM = SYSDATE()
             , PWD_UPD_DTTM = DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
             , LGN_PWD = #{lgn_pwd, jdbcType=VARCHAR}
         WHERE LGN_ID = #{lgn_id, jdbcType=VARCHAR} 
           AND USR_ID = #{usr_id, jdbcType=VARCHAR}

    	/*[BaseTmbrUsrInf.xml][updateTmbrUsrInfPwChgMap][사용자 비밀번호 변경(ParameterMap)][TANINE]*/
        UPDATE TMBR_USR_INF
           SET UPDR_ID = #{updr_id, jdbcType=VARCHAR}
		     , UPD_DTTM = SYSDATE()
             , PWD_UPD_DTTM = DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
             , LGN_PWD = #{lgn_pwd, jdbcType=VARCHAR}
         WHERE LGN_ID = #{lgn_id, jdbcType=VARCHAR} 
           AND USR_ID = #{usr_id, jdbcType=VARCHAR}

/*[BaseTmbrUsrInf.xml][updateTmbrUsrInfLastLogin][최종로그인 정보 변경][TANINE]*/
UPDATE TMBR_USR_INF
   SET LAST_LOGIN_DATE = SYSDATE()
     , IP_ADDR = #{ip_addr, jdbcType=VARCHAR}
 WHERE LGN_ID = #{lgn_id, jdbcType=VARCHAR} 
   AND USR_ID = #{usr_id, jdbcType=VARCHAR}

        /*[BaseTmbrUsrInf.xml][updateTmbrUsrInfPwChgDate][사용자 비밀번호 변경일 변경(ParameterMap)][TANINE]*/
        UPDATE TMBR_USR_INF
           SET UPDR_ID = #{updr_id, jdbcType=VARCHAR}
		     , UPD_DTTM = SYSDATE()
             , PWD_UPD_DTTM = DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
         WHERE LGN_ID = #{lgn_id, jdbcType=VARCHAR} 
           AND USR_ID = #{usr_id, jdbcType=VARCHAR}

        /* [TmbrUsrInfTrx.xml][updateTmbrUsrInfInitUserPwInfo][패스워드 실패횟수 초기화][TANINE] */
        UPDATE TMBR_USR_INF
           SET UPDR_ID = #{updr_id, jdbcType=VARCHAR}
		     , UPD_DTTM = SYSDATE()
		     , PWD_FAIL_CNT = 0
		     , PWD_FAIL_DTTM = NULL

             , ACTV_YN = #{actv_yn, jdbcType=VARCHAR}

         WHERE LGN_ID = #{lgn_id, jdbcType=VARCHAR}
           AND USR_ID = #{usr_id, jdbcType=VARCHAR}

    	/* [TmbrUsrInfTrx.xml][updateInitUserPwInfoAll][사용자 패스워드 정보 초기화][TANINE] */
        UPDATE TMBR_USR_INF
           SET UPDR_ID = #{updr_id, jdbcType=VARCHAR}
		     , UPD_DTTM = SYSDATE()
             , LGN_PWD = #{lgn_pwd, jdbcType=VARCHAR}
             , PWD_FAIL_CNT = 0
		     , PWD_FAIL_DTTM = NULL
             , ACTV_YN = 'Y'
             , PWD_UPD_DTTM = #{pwd_upd_dttm, jdbcType=VARCHAR}
         WHERE LGN_ID = #{lgn_id, jdbcType=VARCHAR}
           AND USR_ID = #{usr_id, jdbcType=VARCHAR}

		/* [TmbrUsrInfTrx.xml][updateTmbrUsrInfMemberLeaveStsByAdmin][회원 상태 코드 탈퇴 처리][TANINE] */
		UPDATE TMBR_USR_INF
		   SET UPDR_ID = #{updr_id, jdbcType=VARCHAR}
		     , UPD_DTTM = SYSDATE()
		 	 , USR_SS_CD = '2'
		 WHERE LGN_ID = #{lgn_id, jdbcType=VARCHAR}
           AND USR_ID = #{usr_id, jdbcType=VARCHAR}
