mapper.sql:
		/*[TsysAdmMnuRtnn.xml][select][TANINE]*/
		SELECT USR_ID
             , MNU_SEQ
             , CRTR_ID
             , CRT_DTTM
             , UPDR_ID
             , UPD_DTTM
          FROM TSYS_ADM_MNU_RTNN
		 WHERE MNU_SEQ = #{mnu_seq, jdbcType=NUMERIC} 
           AND USR_ID = #{usr_id, jdbcType=VARCHAR}

mapper.insert:
        /*[TsysAdmMnuRtnnTrx.xml][insert][TANINE]*/
        INSERT INTO TSYS_ADM_MNU_RTNN
		(
			 USR_ID
		   , MNU_SEQ
		   , CRTR_ID
		   , CRT_DTTM
		   , UPDR_ID
		   , UPD_DTTM
		)
		VALUES
		(
			 #{usr_id, jdbcType=VARCHAR}
		   , #{mnu_seq, jdbcType=NUMERIC}
		   , #{crtr_id, jdbcType=VARCHAR}
		   , SYSDATE()
		   , #{updr_id, jdbcType=VARCHAR}
		   , SYSDATE()
		)

mapper.delete:
        /*[TsysAdmMnuRtnnTrx.xml][delete][TANINE]*/
		DELETE FROM TSYS_ADM_MNU_RTNN
		 WHERE MNU_SEQ = #{mnu_seq, jdbcType=NUMERIC} 
           AND USR_ID = #{usr_id, jdbcType=VARCHAR}

mapper.select:
/*[TsysAdmMnuRtnn.xml][selectTsysAdmMnuRtnnGrdList][관리자메뉴할당 그리드 조회][TANINE]*/ SELECT XXX.* FROM ( SELECT XX.*
, CEIL(XX.ID / #{rows, jdbcType=NUMERIC}) AS PAGE , @ROW_NUMBER AS TOTAL_COUNT , CEIL(@ROW_NUMBER / #{rows, jdbcType=NUMERIC}) AS TOTAL_PAGE
FROM ( SELECT X.* , (@ROW_NUMBER := @ROW_NUMBER + 1) ID FROM ( SELECT T2.USR_ID , T1.PGM_ID , T1.UP_MNU_SEQ , (SELECT S1.MNU_NM FROM TSYS_ADM_MNU S1 WHERE S1.MNU_SEQ = T1.UP_MNU_SEQ) AS UP_MNU_NM , T1.MNU_SEQ , T1.MNU_NM , T2.CRT_DTTM , T1.SYS_CD , (SELECT SYS_NM FROM TDSP_SYS_INF WHERE SYS_CD = T1.SYS_CD) AS SYS_NM FROM TSYS_ADM_MNU T1 , TSYS_ADM_MNU_RTNN T2 WHERE T1.MNU_SEQ = T2.MNU_SEQ AND T1.US_YN = 'Y' AND T1.MNU_YN = 'Y' AND T1.FRNT_YN = 'N' AND T1.MNU_DPTH = 3 AND T2.USR_ID = #{usr_id, jdbcType=VARCHAR} AND T1.SYS_CD = #{sys_cd, jdbcType=VARCHAR} ) X , (SELECT @ROW_NUMBER := 0) RN ) XX
ORDER BY XX.CRT_DTTM
) XXX
WHERE XXX.PAGE = #{page, jdbcType=NUMERIC}

/*[TsysAdmMnuRtnn.xml][selectTsysAdmMnuRtnnYn][관리자메뉴할당 여부 조회][TANINE]*/
SELECT CASE WHEN COUNT(1) > 0 THEN 'Y'
            ELSE 'N'
        END AS RTNN_YN
  FROM TSYS_ADM_MNU_RTNN
 WHERE USR_ID = #{usr_id, jdbcType=VARCHAR}
   AND MNU_SEQ = #{mnu_seq, jdbcType=NUMERIC}
